# Description: An innovative Hybrid Genetic Algorithm with Simulated Annealing for enhanced exploration and exploitation in black box optimization.
# Code:
# ```python
import numpy as np

class HybridGeneticSimulatedAnnealing:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.population_size = 10
        self.mutation_prob = 0.1
        self.crossover_prob = 0.8
        self.lower_bound = -5.0
        self.upper_bound = 5.0
        self.Tmax = 5.0
        self.Tmin = 0.1
        self.alpha = 0.9

    def mutate(self, x):
        return np.clip(x + np.random.normal(0, 1, size=self.dim), self.lower_bound, self.upper_bound)

    def crossover(self, parent1, parent2):
        mask = np.random.choice([0, 1], size=self.dim)
        child = parent1 * mask + parent2 * (1 - mask)
        return np.clip(child, self.lower_bound, self.upper_bound)

    def acceptance_probability(self, energy, new_energy, temperature):
        if new_energy < energy:
            return 1.0
        return np.exp((energy - new_energy) / temperature)

    def anneal(self, energy, candidate, temperature):
        new_candidate = self.mutate(candidate)
        new_energy = func(new_candidate)
        if self.acceptance_probability(energy, new_energy, temperature) > np.random.rand():
            return new_candidate, new_energy
        return candidate, energy

    def __call__(self, func):
        population = np.random.uniform(self.lower_bound, self.upper_bound, size=(self.population_size, self.dim))
        best_solution = population[np.argmin([func(p) for p in population])
        temperature = self.Tmax

        for _ in range(self.budget - self.population_size):
            for i in range(self.population_size):
                candidate = self.mutate(population[i])
                if func(candidate) < func(population[i]):
                    population[i] = candidate
                    if func(candidate) < func(best_solution):
                        best_solution = candidate
                elif self.acceptance_probability(func(population[i]), func(candidate), temperature) > np.random.rand():
                    population[i] = candidate

                parent1, parent2 = np.random.choice(range(self.population_size), 2, replace=False)
                child = self.crossover(population[parent1], population[parent2])
                if func(child) < func(population[parent1]):
                    population[parent1] = child
                    if func(child) < func(best_solution):
                        best_solution = child

                population[parent1], energy = self.anneal(func(population[parent1]), population[parent1], temperature)
                temperature *= self.alpha
                if temperature < self.Tmin:
                    temperature = self.Tmax

        return best_solution
# ```

# Description: A Novel Metaheuristic Algorithm Combining Firefly Algorithm with Differential Evolution for Enhanced Optimization in Black Box Functions
# Code:
# ```python
import numpy as np

class FireflyDEAlgorithm:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.pop_size = 10
        self.beta = 1.0
        self.F = 0.5
        self.CR = 0.3
        self.lower_bound = -5.0
        self.upper_bound = 5.0

    def mutate(self, x, pop):
        idxs = np.random.choice(len(pop), 3, replace=False)
        a, b, c = pop[idxs[0]], pop[idxs[1]], pop[idxs[2]]
        return np.clip(a + self.F * (b - c), self.lower_bound, self.upper_bound) if np.random.rand() < self.CR else x

    def attractiveness(self, i, j):
        return np.exp(-self.beta * np.linalg.norm(i - j))

    def move_fireflies(self, population, gbest):
        for i in range(self.pop_size):
            for j in range(self.pop_size):
                if i != j and func(population[i]) > func(population[j]):
                    attractiveness_ij = self.attractiveness(population[i], population[j])
                    population[i] += attractiveness_ij * (population[j] - population[i])

        return population

    def __call__(self, func):
        population = np.random.uniform(self.lower_bound, self.upper_bound, size=(self.pop_size, self.dim))
        pbest = population.copy()
        gbest = pbest[np.argmin([func(p) for p in pbest])]

        for _ in range(self.budget - self.pop_size):
            population = self.move_fireflies(population, gbest)

            for i in range(self.pop_size):
                candidate = self.mutate(population[i], population)
                if func(candidate) < func(population[i]):
                    population[i] = candidate
                    pbest[i] = candidate
                    if func(candidate) < func(gbest):
                        gbest = candidate

        return gbest
# ```

# Description: A novel hybrid algorithm combining Cultural Algorithms with Harmony Search for enhanced global exploration and exploitation in optimization tasks.
# Code:
# ```python
# import numpy as np

class CulturalHarmonyAlgorithm:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.pop_size = 10
        self.hm_size = 5
        self.max_hm_memory = 3
        self.harmony_rate = 0.6
        self.bandwidth = 0.1
        self.lower_bound = -5.0
        self.upper_bound = 5.0

    def harmony_memory_consideration(self, harmony_memory, new_solution):
        if len(harmony_memory) < self.hm_size:
            harmony_memory.append(new_solution)
        else:
            worst_idx = np.argmax([func(h) for h in harmony_memory])
            if func(new_solution) < func(harmony_memory[worst_idx]):
                harmony_memory[worst_idx] = new_solution

    def __call__(self, func):
        harmony_memory = np.random.uniform(self.lower_bound, self.upper_bound, size=(self.hm_size, self.dim))
        population = np.random.uniform(self.lower_bound, self.upper_bound, size=(self.pop_size, self.dim))

        for _ in range(self.budget - self.pop_size):
            new_solution = np.clip(np.mean(harmony_memory, axis=0) + np.random.uniform(-self.bandwidth, self.bandwidth, size=self.dim), self.lower_bound, self.upper_bound)
            self.harmony_memory_consideration(harmony_memory, new_solution)

            for i in range(self.pop_size):
                if np.random.rand() < self.harmony_rate:
                    population[i] = new_solution if func(new_solution) < func(population[i]) else population[i]

        return population[np.argmin([func(p) for p in population])]
# ```
