# Description: Utilizing a hybrid approach combining Particle Swarm Optimization (PSO) with Differential Evolution (DE) to enhance exploration and exploitation for black box optimization.
# Code: 
# ```python
import numpy as np

class HybridPSODE:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.pop_size = 20
        self.w = 0.5
        self.c1 = 1.5
        self.c2 = 2.0
        self.cr = 0.3

    def __call__(self, func):
        def initialize_population():
            return np.random.uniform(-5.0, 5.0, (self.pop_size, self.dim))

        def evaluate_population(population):
            return np.array([func(individual) for individual in population])

        def mutate(population, best_idx):
            mutant = population[best_idx] + self.w * (population[np.random.choice(len(population))] - population[np.random.choice(len(population))])
            return np.clip(mutant, -5.0, 5.0)

        def recombine(population, target_idx):
            target = population[target_idx].copy()
            indices = np.random.choice(len(population), 3, replace=False)
            for i in range(self.dim):
                if np.random.rand() < self.cr:
                    target[i] = population[indices[0]][i] + self.f * (population[indices[1]][i] - population[indices[2]][i])
            return np.clip(target, -5.0, 5.0)

        population = initialize_population()
        best_idx = np.argmin(evaluate_population(population))
        best_solution = population[best_idx].copy()

        for _ in range(self.budget):
            for i in range(len(population)):
                if np.random.rand() < 0.5:
                    mutant = mutate(population, best_idx)
                    target = recombine(population, i)
                    if func(target) < func(population[i]):
                        population[i] = target
                    else:
                        population[i] = mutant
                else:
                    velocity = self.c1 * np.random.rand() * (best_solution - population[i]) + self.c2 * np.random.rand() * (population[best_idx] - population[i])
                    population[i] = np.clip(population[i] + velocity, -5.0, 5.0)

            new_best_idx = np.argmin(evaluate_population(population))
            if func(population[new_best_idx]) < func(best_solution):
                best_solution = population[new_best_idx].copy()

        return best_solution
# ```
