# Description: Enhancing Novel Adaptive Differential Evolution with Population Diversity Maintenance
# Code:
# ```python
import numpy as np

class EnhancedNovelDEAlgorithm(NovelDEAlgorithm):
    def __init__(self, budget, dim):
        super().__init__(budget, dim)
        self.diversity_factor = 0.5  # Introducing diversity factor

    def __call__(self, func):
        pop = np.random.uniform(-5.0, 5.0, (self.pop_size, self.dim))
        fitness = np.array([func(ind) for ind in pop])

        for _ in range(self.budget):
            for i in range(self.pop_size):
                idxs = np.random.choice(list(range(self.pop_size)), 3, replace=False)
                x_r1, x_r2, x_r3 = pop[idxs]
                mutant = pop[i] + self.f * (x_r1 - pop[i]) + self.f * (x_r2 - x_r3)
                for j in range(self.dim):
                    if np.random.rand() > self.cr:
                        mutant[j] = pop[i][j]
                mutant_fitness = func(mutant)
                if mutant_fitness < fitness[i]:
                    pop[i] = mutant
                    fitness[i] = mutant_fitness
            # Introduce diversity maintenance
            mean = np.mean(pop, axis=0)
            for i in range(self.pop_size):
                pop[i] = pop[i] + self.diversity_factor * (mean - pop[i])
        return pop[np.argmin(fitness)]
# ```

# Description: Implementing a Novel Metaheuristic Algorithm based on Firefly Algorithm for Black Box Optimization
# Code: 
# ```python
import numpy as np

class FireflyAlgorithm:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.pop_size = 10
        self.alpha = 0.2
        self.beta_min = 0.2
        self.beta_max = 0.9

    def attractiveness(self, x, y):
        return np.exp(-np.linalg.norm(x - y))

    def levy_flight(self):
        beta = np.random.uniform(self.beta_min, self.beta_max)
        return np.random.standard_cauchy(self.dim) / (1 + beta)

    def __call__(self, func):
        pop = np.random.uniform(-5.0, 5.0, (self.pop_size, self.dim))
        fitness = np.array([func(ind) for ind in pop])

        for _ in range(self.budget):
            for i in range(self.pop_size):
                for j in range(self.pop_size):
                    if fitness[j] < fitness[i]:
                        attractiveness_ij = self.attractiveness(pop[i], pop[j])
                        step = self.alpha * (pop[j] - pop[i]) * attractiveness_ij + self.levy_flight()
                        new_pos = pop[i] + step
                        new_fitness = func(new_pos)
                        if new_fitness < fitness[i]:
                            pop[i] = new_pos
                            fitness[i] = new_fitness
        return pop[np.argmin(fitness)]
# ```

# Description: Utilizing a dynamic ensemble approach with multiple mutation strategies to enhance diversity and exploration capabilities.
# Code:
# ```python
import numpy as np

class DynamicEnsembleNovelDEAlgorithm:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.pop_size = 10
        self.cr = 0.5
        self.f = 0.5
        self.sigma = 0.1
        self.sigma_min = 0.01
        self.sigma_max = 0.2
        self.cr_min = 0.2
        self.cr_max = 0.9
        self.strategies = [self.strategy1, self.strategy2, self.strategy3]

    def __call__(self, func):
        pop = np.random.uniform(-5.0, 5.0, (self.pop_size, self.dim))
        fitness = np.array([func(ind) for ind in pop])

        for _ in range(self.budget):
            for i in range(self.pop_size):
                idxs = np.random.choice(list(range(self.pop_size)), 3, replace=False)
                x_r1, x_r2, x_r3 = pop[idxs]
                strategy = np.random.choice(self.strategies)  # Select random mutation strategy
                mutant = strategy(pop[i], x_r1, x_r2, x_r3)
                mutant_fitness = func(mutant)
                if mutant_fitness < fitness[i]:
                    pop[i] = mutant
                    fitness[i] = mutant_fitness
        return pop[np.argmin(fitness)]

    def strategy1(self, current, x_r1, x_r2, x_r3):
        return current + self.f * (x_r1 - current) + self.f * (x_r2 - x_r3) + np.random.normal(0, self.sigma, self.dim)

    def strategy2(self, current, x_r1, x_r2, x_r3):
        return current + 2*self.f * (x_r1 - current) + np.random.normal(0, self.sigma, self.dim)

    def strategy3(self, current, x_r1, x_r2, x_r3):
        return current + self.f * (x_r1 - current) + 3*self.f * (x_r2 - x_r3) + np.random.normal(0, self.sigma, self.dim)
